mapper.select:
/* LoyCustomerMapper.selectCustList */ select a.rid as rid /* RID */ , a.cust_type_cd as custTypeCd /* 고객 유형 코드 */ , cd1.mark_name as custTypeNm /* 고객유형명*/ , a.cust_nm as custNm , a.real_birth_dt as realBirthDt , a.birth_dt as birthDt /* 생일 */ , a.gen_gbn_cd as genGbnCd /* 성별 구분 코드 */ , cd2.mark_name as genGbnNm /* 성별 */ , a.lunar_gbn_cd as lunarGbnCd /* 양음력 구분 코드 */ , a.identi_type_cd as identiTypeCd /* 식별 유형 코드 */ , cd.mark_name as identiTypeNm /* 식별 유형 명 */ , a.identi_val as identiVal /* 식별 값 */ , a.realnm_certi_yn as realnmCertiYn /* 실명 인증 여부 */ , CONVERT(varchar(10), a.realnm_certi_date, 120) as realnmCertiDate , CONVERT(varchar(10), a.create_date, 120) as createDate , CONVERT(varchar(10), a.modify_date, 120) as modifyDate , em.name as createBy , em1.name as modifyBy ,${strColumn} from ( select rid , cust_type_cd , gen_gbn_cd , lunar_gbn_cd , identi_type_cd , case identi_type_cd when '01' then '**********' when '02' then 'PHONE_NUM2' when '03' then 'CARD_NUM' when '04' then 'PASSPORT_NUM' when '05' then 'L_PERSON_NUM' else '****' end as identi_val /* 식별 값 */ , identi_val as identi_val_decoding /* 암호화되지 않은 식별 값 */ , realnm_certi_yn , realnm_certi_date
, flag , create_date, create_by, modify_date, modify_by , a.cust_nm as cust_nm , a.cust_nm as cust_nm_decoding /* 암호화되지 않은 고객 명 */ , concat(substring(a.real_birth_dt, 1, 4) , com.fn_rpad(substring(a.real_birth_dt, 5, 0), len(a.real_birth_dt) - 4, '*')) as real_birth_dt /* 실 생년월일 */ , concat(substring(a.birth_dt, 1, 4) , com.fn_rpad(substring(a.birth_dt, 5, 0), len(a.birth_dt) - 4, '*')) as birth_dt /* 생년월일 */ from loy.loy_cust a WITH (NOLOCK) where ${strCondWhere} and ${strSVCType} and ${strWhere}
and a.flag = 1 ) a left join com.crm_user us WITH (NOLOCK) on a.create_by = us.rid left join com.crm_user us1 WITH (NOLOCK) on a.modify_by = us1.rid left join com.employee em WITH (NOLOCK) on us.id_employee = em.id left join com.employee em1 WITH (NOLOCK) on us1.id_employee = em1.id left join com.comm_code cd WITH (NOLOCK) on a.identi_type_cd = cd.code_name and cd.group_code = 'LOY_IDENTI_TYPE_CD' and cd.flag = 1 and cd.lang = #{lang} left join com.comm_code cd1 WITH (NOLOCK) on a.cust_type_cd = cd1.code_name and cd1.group_code = 'LOY_CUST_TYPE_CD' and cd1.flag = 1 and cd1.lang = #{lang} left join com.comm_code cd2 WITH (NOLOCK) on a.gen_gbn_cd = cd2.code_name and cd2.group_code = 'LOY_GEN_CD' and cd2.flag = 1 and cd2.lang = #{lang} ${strEndPaging}

mapper.update:
		/* LoyCustomerMapper.updateCust */
		UPDATE
		 	loy.loy_cust
		SET
			cust_nm 					= #{custNm}
		    , cust_type_cd 			= #{custTypeCd}
		    , identi_type_cd 		= #{identiTypeCd}
			, identi_val 				= #{identiVal}
			, realnm_certi_yn 		= #{realnmCertiYn}
			, realnm_certi_date 	= #{realnmCertiDate}
			, birth_dt					= #{birthDt}
			, gen_gbn_cd 			= #{genGbnCd}
			, real_birth_dt  			= #{realBirthDt}
			, lunar_gbn_cd 			= #{lunarGbnCd}
            , modify_date 			= getDate()
            , modify_by 				= #{modifyBy}
		where 1 = 1
		  and rid	= #{rid}

mapper.insert:
		/* LoyCustomerMapper.insertCust */
		insert into loy.loy_cust (
			     rid
				,cust_type_cd
				,cust_nm
				,birth_dt
				,gen_gbn_cd
				,real_birth_dt
				,lunar_gbn_cd
				,identi_type_cd
				,identi_val
				,realnm_certi_yn
				,realnm_certi_date



                ,create_date
                ,create_by
				,flag
		) values (
			     #{rid} 
               , #{custTypeCd}
               , #{custNm}
               , #{birthDt}
               , #{genGbnCd}
               , #{realBirthDt}
               , #{lunarGbnCd}
               , #{identiTypeCd}
               , #{identiVal}
               , #{realnmCertiYn}
               , CONVERT(varchar(10),#{realnmCertiDate}, 120) 



               , getDate()
               , #{createBy}
               , 1
		)

			select 1 from dual

mapper.select:
       	/* LoyCustomerMapper.duplCust */
       	SELECT 
       		count(1)
		FROM dual
		WHERE EXISTS (
		 select 1 from loy.loy_cust a
		 where 1=1

		 and a.identi_type_cd = #{identiTypeCd}
		 and a.identi_val 		= #{identiVal}
		 and a.flag = 1
		)

mapper.update:
/* LoyCustomerMapper.updateCustDel */
UPDATE 
	loy.loy_cust
SET 
	 modify_date	= getDate()
	,modify_by	= #{modifyBy}
    ,flag				= 0
 WHERE 
 	1 = 1
   	and rid = #{rid}

mapper.insert:
/* LoyCustomerMapper.insertCustAddInfo */
insert into loy.loy_cust_add_info
