		)
		values(
		, #{rid}
		, #{ridCust}
		, #{addInfoTypeCd}
		, #{attrTypeCd}
		, #{attrVal}



		, getDate()
		, #{createBy}
		, #{modifyBy}
		)

	select 1 from dual

mapper.update:
		/* LoyCustomerMapper.updateCustAddInfo */
		update 
			loy.loy_cust_add_info
		set   
			add_info_type_cd 	= #{addInfoTypeCd}
		    , attr_type_cd 		= #{attrTypeCd}
			, attr_val 				= #{attrVal}

            , modify_date 		= getDate()
            , modify_by 			= #{modifyBy}
		where 1 = 1
		  and rid	= #{rid}

		/* LoyCustomerMapper.updateCustAddInfoDel */
		UPDATE 
			loy.loy_cust_add_info
		SET   
			flag = flag + 1
            , modify_date = getDate()
            , modify_by 	 = #{modifyBy}
		WHERE 
			1 = 1
		  	and rid	= #{rid}

mapper.select:
/* LoyCustomerMapper.selectCustDetailNewPage */ SELECT TOP 1 lc.CUST_NO AS custNo , lc.CUST_TYPE_CD AS custTypeCd , lc.CUST_STAT_CD AS custStatCd , lc.CUST_NM AS cust_nm , lc.GEN_CD AS gen_cdNm , lci.NLTY AS nltyCd , lci.CUST_NM_ADD AS cust_nm_add
, lc.BIRTHDT AS birthdt , lc.LUNAR_CD AS lunar_cd , lc.HHP AS hhp , lc.EMAIL AS email , lc.ZIP AS zip , lc.ADRES AS adres , lc.BUNJI AS bunji , lci.ZIP_ADD AS zip_add , lci.ADRES_ADD AS adres_add , lci.BUNJI_ADD AS bunji_add , CONVERT(varchar(10), lc.SBSC_DATE, 120) AS sbscDate , lc1.CHNL_NM AS sbscChnlNm , lc2.CHNL_NM AS lastChnlNm , lc.SBSC_PATH_CD AS sbscPathCd , lc.SBSC_DEVICE_CD AS sbscDeviceCd , CONVERT(varchar(10), lcsh.SVC_DT, 120) AS svrDt
, lc.RCMMD_NO AS rcmmdNo , lc.DRMNCY_CHNG_UNIT AS drmmcyChngUnitCd , lcwi.USER_ID AS user_id
, lc.CERT_TYPE_CD AS custCertTypeCd , lc.CERT_SUB_TYPE_CD AS certSubTypeCd
, CONVERT(varchar, lc.CERT_DATE, 120) AS certDate
, lci.ANNVRSRY AS annvrsry , lci.ANNVRSRY_TYPE_CD AS annvrsryTypeCd , lci.WRC_NM AS wrc_nm , lci.PSTN AS pstnCd , lci.MRRG_YN AS mrrg_yn , lci.NATIVE_YN AS native_yn , lc3.CHNL_NM AS lastConChnlNm /* 최근접속채널 */ , CONVERT(varchar(10), lcsh1.SVC_DT, 120) AS conSvrDt /* 최근접속일 */ , lc4.CHNL_NM AS lastPurChnlNm /* 최근구매채널 */ , CONVERT(varchar(10), lcsh2.SVC_DT, 120) AS purSvrDt /* 최근접속일 */ , lp1.PGM_NM AS joinMembership , lc.RID AS rid FROM LOY.LOY_CUST lc WITH (NOLOCK) LEFT OUTER JOIN LOY.LOY_CUST_I lci WITH (NOLOCK) ON lc.RID = lci.RID_CUST AND lci.FLAG = 1 LEFT OUTER JOIN LOY.LOY_CHNL lc1 WITH (NOLOCK) ON lc.RID_SBSC_CHNL = lc1.RID AND lc1.FLAG = 1 LEFT OUTER JOIN LOY.LOY_CUST_LOGIN_INF lcwi WITH (NOLOCK) ON lc.RID = lcwi.RID_CUST AND lcwi.FLAG = 1 LEFT OUTER JOIN LOY.LOY_CUST_SVC_HIST lcsh WITH (NOLOCK) ON lc.RID = lcsh.RID_CUST AND lcsh.FLAG = 1 LEFT OUTER JOIN LOY.LOY_CHNL lc2 WITH (NOLOCK) ON lc2.RID = lcsh.RID_CHNL AND lc2.FLAG = 1 LEFT OUTER JOIN LOY.LOY_CUST_SVC_HIST lcsh1 WITH (NOLOCK) ON lc.RID = lcsh1.RID_CUST AND lcsh1.FLAG = 1 AND lcsh1.SVC_USE_CD ='WEB' LEFT OUTER JOIN LOY.LOY_CUST_SVC_HIST lcsh2 WITH (NOLOCK) ON lc.RID = lcsh2.RID_CUST AND lcsh2.FLAG = 1 AND lcsh2.SVC_USE_CD ='PUR' LEFT OUTER JOIN LOY.LOY_CHNL lc3 WITH (NOLOCK) ON lc3.RID = lcsh1.RID_CHNL AND lc3.FLAG = 1 LEFT OUTER JOIN LOY.LOY_CHNL lc4 WITH (NOLOCK) ON lc4.RID = lcsh2.RID_CHNL AND lc4.FLAG = 1 LEFT OUTER JOIN LOY.LOY_PGM lp1 WITH (NOLOCK) ON lp1.RID = lc.RID_SBSC_PGM AND lp1.FLAG = 1 WHERE lc.FLAG = 1 AND lc.CUST_STAT_CD in ('A','D') /* 활성고객만*/ AND lc.rid = #{rid}

/* LoyCustomerMapper.selectCertList */
SELECT  lcct.CERT_TYPE_CD							AS certTypeCd
	  , lcct.CERT_VAL										AS certVal
	  , CONVERT(varchar, lcct.CERT_DATE, 120) AS certDate
	  , lcct.rid													AS rid 
	  , ${strColumn}
FROM  
	LOY.LOY_CUST_CERT lcct
WHERE 
	lcct.FLAG = 1
	AND   lcct.RID_CUST = #{rid}
	AND    ${strCondWhere}
	AND    ${strWhere}
order by 
	${strOrderby}
	${strEndPaging}

/* LoyCustomerMapper.selectCustLoginList */
SELECT    
		  lc.CUST_NO							AS custNo
		, lcwh.WEB_USE_CD				AS webUseCd
		, lcwh.WEB_INPUT_CD			AS webInputCd
		, lcwh.CREATE_DATE				AS createDate
		, lcwh.RID								AS rid
		, ${strColumn}
FROM   
	LOY.LOY_CUST_WEB_HIST lcwh WITH (NOLOCK)
JOIN loy.LOY_CUST lc WITH (NOLOCK) ON lcwh.RID_CUST = lc.RID AND lc.FLAG = 1
WHERE  
	lcwh.FLAG = 1  
	AND    lc.CUST_NO = #{custNo}
	AND    ${strCondWhere}
	AND    ${strWhere}
order by 
	${strOrderby}
	${strEndPaging}

/* LoyCustomerMapper.selectCustSvrHistList */
SELECT     
		   lcsh.SVC_USE_CD															 	 AS svrTypeCd
      	 , lc.CHNL_NM																		 AS chnlNm
      	 , lc.CHNL_TYPE_CD																 AS chnlTypeCd
      	 , CONVERT(varchar(10), lcsh.SVC_DT, 120) 							 AS svrDt
      	 , lcsh.RID		 																		 AS rid
      	 , ${strColumn} 
FROM   
	LOY.LOY_CUST_SVC_HIST lcsh WITH (NOLOCK)
LEFT OUTER JOIN LOY.LOY_CHNL lc WITH (NOLOCK) ON lc.RID = lcsh.RID_CHNL AND lc.FLAG = 1
WHERE ${strCondWhere}
AND   ${strWhere}
AND   lcsh.FLAG = 1
AND   lcsh.RID_CUST = #{rid}
ORDER BY 
	${strOrderby}
	${strEndPaging}

/* LoyCustomerMapper.selectAppSvrHistList */
SELECT    
		  lmah.REG_DATE							AS regDate
		, lmah.MOBILE_DEVICE_CD			AS mobileDeviceCd
		, lmah.VER_NO								AS verNo
		, lmah.TOKEN_VAL							AS tokenVal
		, ${strColumn}
FROM   
		LOY.LOY_MOBILE_APP_HIST lmah
WHERE 
		${strWhere}
	AND   lmah.FLAG = 1
	AND   lmah.RID_CUST = #{rid}
ORDER BY 
	${strOrderby}
	${strEndPaging}

/* LoyCustomerMapper.selectCustDetailMbrList */
SELECT lm.MBR_NO 											AS mbrNo
		, lp.PGM_NM 												AS pgmNm
		, lm.MBR_STAT_CD  									AS mbrStatCd
		, CONVERT(varchar, lm.SBSC_DATE, 120)   AS sbscDate
		, lch.CHNL_NM 											AS chnlNm
		, lm.RID 													AS rid
		, ${strColumn}
FROM loy.LOY_CUST lc WITH (NOLOCK)
INNER JOIN loy.LOY_MBR lm WITH (NOLOCK) ON lc.rid = lm.RID_CUST AND lm.FLAG = 1
INNER JOIN loy.LOY_PGM lp WITH (NOLOCK) ON lm.rid_pgm = lp.RID AND lp.FLAG = 1
INNER JOIN loy.LOY_CHNL lch WITH (NOLOCK) ON lm.RID_SBSC_CHNL = lch.RID AND lch.FLAG = 1
WHERE ${strWhere}
AND lc.FLAG = 1
AND lc.RID = #{ridCust}
ORDER BY 
	${strOrderby}
	${strEndPaging}

/* LoyCustomerMapper.selectCustLoginInfo */
SELECT  
		lcli.LOGIN_TYPE_CD								AS loginTypeCd
	  , lcli.USER_ID											AS userId
	  , lcli.FAIL_CNT											AS failCnt
	  , CONVERT(varchar, lcli.FAIL_DATE, 120)   AS failDate
	  , ${strColumn}
FROM  
		LOY.LOY_CUST_LOGIN_INF lcli
WHERE 
	${strWhere}
	AND lcli.FLAG = 1
	AND lcli.RID_CUST = #{rid}
ORDER BY 
	${strOrderby}
	${strEndPaging}

/* LoyCustomerMapper.selectCustBranchList */
SELECT
	A.RID                                     						AS rid,
	A.BR_NO                                   					AS brNo,
	A.BR_NM                                   					AS brNm,
	B.CHNL_NO                                  			 		AS chnlNo,
	B.CHNL_NM                                   				AS chnlNm,
	A.CHARGER                                   				AS charger,
	A.TEL_NO                                      				AS telNo,
	CONVERT(varchar(10), A.CREATE_DATE, 120) AS createDate
	${strColumn}
FROM LOY.LOY_MBR_C_BR A WITH (NOLOCK)
INNER JOIN LOY.LOY_CHNL B WITH (NOLOCK) ON A.RID_CHNL = B.RID AND B.FLAG = 1
WHERE  ${strCondWhere}
AND ${strWhere}
AND A.FLAG  = 1
AND A.RID_MBR_C = #{ridMbrC}
ORDER BY ${strOrderby}
${strEndPaging}

mapper.update:
/* LoyCustomerMapper.updateCustBranchDel */
UPDATE 
	LOY.LOY_MBR_C_BR
SET 
	MODIFY_DATE	= getDate()
	,MODIFY_BY		= #{modifyBy}
	,FLAG				= 0
WHERE 
	1 = 1
	AND RID = #{rid}

mapper.select:
/* LoyCustomerMapper.selectCustBnftList */
SELECT
	A.RID                               								AS rid,
	CONVERT(varchar(10), A.CREATE_DATE, 120) AS createDate,
	CONVERT(varchar(10), A.START_DATE, 120) 	AS startDate,
	CONVERT(varchar(10), A.END_DATE, 120) 		AS endDate,
	A.QTY                               								AS qty,
	A.RID_OFR                          							AS ridOfr,
	B.OFR_NO                            							AS ofrNo,
	B.OFR_NM                            							AS ofrNm,
	${strColumn}
FROM
LOY.LOY_MBR_C_BNFT A WITH (NOLOCK)
INNER JOIN LOY.LOY_OFR B WITH (NOLOCK) ON A.RID_OFR = B.RID AND B.FLAG = 1
WHERE ${strCondWhere}
AND ${strWhere}
AND A.FLAG = 1
AND A.RID_MBR_C = #{ridMbrC}
ORDER BY ${strOrderby}
${strEndPaging}

mapper.update:
/* LoyCustomerMapper.updateCustBnftDel */
UPDATE 
	LOY.LOY_MBR_C_BNFT
SET 
	MODIFY_DATE = getDate()
	,MODIFY_BY = #{modifyBy}
	,flag = flag + 1
WHERE 
	1 = 1
AND RID = #{rid}

/* LoyCustomerMapper.updateCustSettleDel */
UPDATE 
	LOY.LOY_MBR_C_SETTLE
SET 
	 MODIFY_DATE = getDate()
	,MODIFY_BY = #{modifyBy}
	,flag = flag + 1
WHERE 
	1 = 1
	AND RID = #{rid}

mapper.select:
	/* LoyCustomerMapper.selectCustWebHistList */
	SELECT 
		  lcwh.RID 																AS rid
		, lcwh.SESSION_ID 												AS sessionId
		, lcwh.ACCESS_TYPE_CD 										AS accessTypeCd
		, lcwh.LOG_TYPE_CD 												AS logTypeCd
		, lcwh.AC_DEVICE_CD 											AS acDeviceCd
		, lc.CHNL_NM 														AS chnlNm
		, lc.CHNL_NO 														AS chnlNo
		, CONVERT(varchar,lcwh.CREATE_DATE, 120)  			AS createDate
		, ${strColumn}
	FROM 
			loy.LOY_CUST_WEB_HIST lcwh WITH (NOLOCK)
		JOIN loy.LOY_CHNL lc WITH (NOLOCK) ON lcwh.RID_CHNL = lc.RID AND lc.FLAG =1 
		WHERE ${strWhere}
		AND lcwh.RID_CUST = #{rid}
		ORDER BY 
    		${strOrderby}
    		${strEndPaging}
