		, lpui.CI AS ci
		, lpui.rid AS rid
		, lo.OFR_NO		AS ofrNo
		, lo.OFR_NM		AS ofrNm
		, lo.RID		AS ofrRid
		, t1.totalCount
from 	(	SELECT 	*
			from 	LOY.LOY_PNT_UPLOAD_ITEM lpui WITH (NOLOCK)
			where 	${strCondWhere}
				and lpui.rid_pnt_upload = #{ridPntUpload}
				and lpui.flag = 1

				and lpui.batch_result_cd = #{batchResultCd}


				ORDER BY ${strOrderby}
				${strEndPaging}

		) lpui
LEFT JOIN LOY.LOY_MBR lm WITH (NOLOCK) ON lm.RID = lpui.RID_MBR
LEFT JOIN LOY.LOY_CUST lc WITH (NOLOCK) ON lc.RID = lm.RID_CUST
JOIN LOY.LOY_PNT_UPLOAD lpu WITH (NOLOCK) ON lpui.RID_PNT_UPLOAD = lpu.RID AND lpu.FLAG = 1
LEFT OUTER JOIN LOY.LOY_OFR lo WITH (NOLOCK) ON lpu.RID_OFFER = lo.RID AND lo.FLAG = 1
, (
	SELECT 	/*+ INDEX(lpui RID_PNT_UPLOAD_IDX) */
			COUNT(1) AS totalCount
	FROM 	LOY.LOY_PNT_UPLOAD_ITEM lpui WITH (NOLOCK)
	WHERE 	lpui.RID_PNT_UPLOAD = #{ridPntUpload}
		AND FLAG = 1
) t1
where 	${strWhere}

	ORDER BY ${strOrderby}
	${strEndPaging}


	ORDER BY ${strOrderby}

