	, RID_UPLD_EMP
	, FILE_UPLD_TOT_CNT
	, POINT_TRT_STATUS
) VALUES (
	#{rid}
	, #{createBy}
	, #{modifyBy}
	, GETDATE()
	, GETDATE()
	, 1
	, #{filePath}
	, #{fileName}
	, GETDATE()
	, #{createBy}
	, #{uploadCnt}
	, 'W'
)

mapper.update:
/*SystemBatchRgstMapper./updatePntUploadResult*/
UPDATE 	LOY.LOY_UPLD_FILE
SET 	POINT_TRT_STATUS = #{batchResultCd}
		, MODIFY_DATE = GETDATE()
WHERE 	rid = #{rid}

/* SystemBatchRgstMapper.updateSmsUnSubUploadCnt */
UPDATE LOY.LOY_UPLD_FILE
SET FILE_UPLD_SUCES_CNT = #{uploadCnt}
	     , FILE_UPLD_FAIL_CNT  = #{failUploadCnt}
		 , modify_date = GETDATE()
WHERE RID = #{rid}

mapper.select:
/* SystemBatchRgstMapper.selectValidMbrDetailInfo2 */
SELECT lm.mbr_no as mbrNo
    	,  lm.rid    as rid
  FROM LOY.LOY_MBR LM with (nolock)
  JOIN LOY.LOY_CUST LC with (nolock) ON LM.RID_CUST = LC.RID AND LC.FLAG = 1
  WHERE LM.FLAG = 1
   AND LM.MBR_STAT_CD = 'A'
   AND LC.CUST_NM = #{custNm}
   AND LC.HHP = #{hhpNo}

/* SystemBatchRgstMapper.selectValidMbrDetailCnt */
SELECT count(*)
  FROM LOY.LOY_MBR LM with (nolock)
  JOIN LOY.LOY_CUST LC with (nolock) ON LM.RID_CUST = LC.RID AND LC.FLAG = 1
  WHERE LM.FLAG = 1
   AND LM.MBR_STAT_CD = 'A'
   AND LC.CUST_NM = #{custNm}
   AND LC.HHP = #{hhpNo}
