   where mei.cam_id = #{camId}
   and rownum=1

    /* LoyCampaign.checkElecAprov */
	    select CASE when (cc.code_name = '090' or cc.code_name = '002') then 'true' else 'false' end as checkVal
		from mkt.mkt_cam_mst mcm
		join com.com_elec_aprv mea on mcm.id = mea.cam_id and mea.flag = 1
		join com.comm_code cc on cc.code_name = mea.elec_aprv_status_cd and cc.lang = #{lang} and cc.group_code = 'EL_APPROVAL_STATUS_CD'
		where mcm.id = #{camId}

/* LoyCampaignMapper.selectCampaignDefaultInfo */ SELECT lp.PROM_NO AS promNo , lp.PROM_NM AS promNm , lp.DESC_TEXT AS descText , lp.PROM_STAT_CD AS promStatCd , lp.RID_PGM AS pgmRid , lpp.PGM_NM AS pgmNm , lp.PROM_GOAL_CD AS promGoalCd , lp.MAX_REWARD_CNT AS maxRewardCnt , lp.ISS_DUP_YN AS issDupYn , lp.PROM_TYPE_LV1_CD AS promTypeLv1Cd , lp.PROM_TYPE_LV2_CD AS promTypeLv2Cd , lp.RID_OFR AS ofrRid , lo.OFR_NM AS ofrNm , lo.OFR_NO AS ofrNo , lo.CPN_YN AS cpnYn
, lp.PROM_OFFER_AMT_DEF_CD AS promOfferAmtDefCd , TO_CHAR(lp.VALID_START_DATE,'YYYY-MM-DD HH24:MI:SS') AS validStartDate , TO_CHAR(lp.VALID_END_DATE, 'YYYY-MM-DD HH24:MI:SS') AS validEndDate , lp.PROM_CYCLE_CD AS promCycleCd , lp.CYCLE_MON_YN AS cycleMonYn , lp.CYCLE_TUE_YN AS cycleTueYn , lp.CYCLE_WED_YN AS cycleWedYn , lp.CYCLE_THU_YN AS cycleThuYn , lp.CYCLE_FRI_YN AS cycleFriYn , lp.CYCLE_SAT_YN AS cycleSatYn , lp.CYCLE_SUN_YN AS cycleSunYn , lp.SPECIAL_DAY_FROM AS specialDayFrom , lp.SPECIAL_DAY_TO AS specialDayTo , lp.SPECIAL_TIME_FROM AS specialTimeFrom , lp.SPECIAL_TIME_TO AS specialTimeTo , lp.PROM_CALC_CD AS promCalcCd , lp.RID_TGT_GRP_INC AS tgtGrpRid , lp.RID_TGT_GRP_EXC AS expectTgtGrpRid , ltg.TGT_GRP_NO AS tgtGrpNo , ltg.TGT_GRP_NM AS tgtGrpNm , eltg.TGT_GRP_NO AS expectTgtGrpNo , eltg.TGT_GRP_NM AS expectTgtGrpNm , lp.MODIFY_DATE AS modifyDate , lp.CREATE_DATE AS createDate , em.name AS name , cd.DIV_NM AS divNm , lp.RID AS rid , lp.RID AS id , lp.RID_CHNL_GRP AS chnlGrpRid , lcg.TGT_GRP_NO AS chnlGrpNo , lcg.TGT_GRP_NM AS chnlGrpNm , lp.noti_yn AS notiYn FROM LOY.LOY_PROM lp LEFT OUTER JOIN LOY.LOY_PGM lpp ON lp.RID_PGM = lpp.RID AND lpp.FLAG = 1 LEFT OUTER JOIN LOY.LOY_OFR lo ON lp.RID_OFR = lo.RID AND lo.FLAG = 1
LEFT OUTER JOIN LOY.LOY_TGT_GRP ltg ON lp.RID_TGT_GRP_INC = ltg.RID AND ltg.FLAG = 1 LEFT OUTER JOIN LOY.LOY_TGT_GRP eltg ON lp.RID_TGT_GRP_EXC = eltg.RID AND eltg.FLAG = 1 LEFT OUTER JOIN COM.CRM_USER cu ON cu.RID = lp.CREATE_BY AND cu.FLAG = 1 LEFT OUTER JOIN COM.EMPLOYEE em ON em.ID = cu.ID_EMPLOYEE AND em.FLAG = 1 LEFT OUTER JOIN COM.CRM_DIVISION cd ON cd.RID = em.RID_DIVISION AND cd.FLAG = 1 LEFT OUTER JOIN LOY.LOY_CHNL_GRP lcg ON lp.RID_CHNL_GRP = lcg.RID AND lcg.FLAG = 1 WHERE lp.FLAG = 1 AND lp.RID = #{rid}

