mapper.select:
/* LoyCampaignMapper.selectCampaignWorkflowStatusList */ select wfc.ID , wfc.CREATE_BY as createBy , wfc.MODIFY_BY as modifyBy , wfc.CREATE_DATE as createDate , wfc.MODIFY_DATE as modifyDate
, wfc.CAM_ID as campaignId , wfc.WF_ID as workflowId
, c1.CODE_NAME as statusCode , c1.MARK_NAME as statusCodeName
, wfc.TASK_ID as taskId , wfc.TASK_NM as taskName , wfc.TASK_DESC as taskDesc
, c2.CODE_NAME as taskTypeCode , c2.MARK_NAME as taskTypeCodeName
, c3.CODE_NAME as nodeTypeCode , c3.MARK_NAME as nodeTypeCodeName
, wfc.ACT_TASK_ID as actualTaskId
from loy.loy_cam_wf_rel wfc left outer join com.comm_code c1 on c1.flag = 1 and c1.lang = #{lang} and c1.code_name = wfc.TASK_PROGR_STATUS_CD and c1.group_code = 'MKT_TASK_TYPE' left outer join com.comm_code c2 on c2.flag = 1 and c2.lang = #{lang} and c2.code_name = wfc.TASK_TYPE_CD and c2.group_code = 'MKT_WF_NODE_TYPE' left outer join com.wf_task_node wtn on wtn.id = wfc.task_id left outer join com.wf_node_mst wn on wn.id = wtn.node_id left outer join com.comm_code c3 on c3.flag = 1 and c3.lang = #{lang} and c3.code_name = wn.NODE_TYPE_CD and c3.group_code = 'MKT_WF_NODE_TYPE' where ${strSVCType} and wfc.flag = 1 AND wfc.WF_ID = #{workflowId}
AND wfc.CAM_ID = #{campaignId}
order by c3.SEQ

