mapper.select:
        /* LoyCampaignMapper.selectPromotionListNew */
       select  	lp2.PGM_NM			AS pgmNm
       		  ,	lp.PROM_NM			AS promNm
       		  , lp.PROM_TYPE_LV1_CD	AS promTypeLv1Cd
       		  , lp.PROM_TYPE_LV2_CD AS promTypeLv2Cd
       		  , lp.PROM_STAT_CD		AS promStatCd
       		  , TO_CHAR(lp.VALID_START_DATE,'YYYY-MM-DD')	AS validStartDate
       		  , TO_CHAR(lp.VALID_END_DATE,'YYYY-MM-DD')	AS validEndDate
       		  , em.name				AS name
       		  , TO_CHAR(lp.CREATE_DATE,'YYYY-MM-DD HH24:MI:SS') AS createDate
       		  , lp.RID				AS rid
       		  , lp.PROM_NO AS promNo
       		  , ${strColumn}
       FROM  LOY.LOY_PROM lp
       LEFT OUTER JOIN LOY.LOY_PGM lp2 ON lp.RID_PGM = lp2.RID AND lp2.FLAG = 1
       LEFT OUTER JOIN com.CRM_USER cu ON cu.RID = lp.CREATE_BY AND cu.FLAG = 1
	   LEFT OUTER JOIN com.EMPLOYEE em ON em.ID = cu.ID_EMPLOYEE AND em.FLAG = 1
       WHERE lp.FLAG = 1
	  and ${strCondWhere}
	  and ${strDataAuthWhere}
	  and ${strWhere}
 	  order by ${strOrderby}
	  ${strEndPaging}

