mapper.select:
        /* LoyCampaignMapper.selectPromotionListPop */
       select   lp.PROM_NM			AS promNm
       		  , lp.PROM_TYPE_LV1_CD	AS promTypeLv1Cd
       		  , lp.PROM_TYPE_LV2_CD AS promTypeLv2Cd
       		  , lp.PROM_STAT_CD		AS promStatCd
       		  , lo.OFR_NO			AS ofrNo
       		  , lo.OFR_NM			AS ofrNm
       		  , TO_CHAR(lp.VALID_START_DATE,'YYYY-MM-DD')	AS validStartDate
       		  , TO_CHAR(lp.VALID_END_DATE,'YYYY-MM-DD')	AS validEndDate
       		  , lo.OFR_NM			AS ofrNm
       		  , lo.OFR_TYPE			AS ofrTypeCd
       		  , lp.RID				AS rid
       		  , lp.RID_OFR			AS ofrRid
       		  , ${strColumn}
       FROM  LOY.LOY_PROM lp
       JOIN LOY.LOY_OFR lo ON lp.RID_OFR = lo.RID AND lo.FLAG = 1
       WHERE lp.FLAG = 1
       AND   lp.PROM_STAT_CD = 'A'

       AND   lp.PROM_TYPE_LV2_CD = #{promType2Cd}

	  and ${strCondWhere}
	  and ${strWhere}
 	  order by ${strOrderby}
	  ${strEndPaging}

mapper.update:
/* LoyCampaignMapper.deleteCampaignContentsChnnelRelByCampaignId */
update LOY.LOY_PROM_ALT_CHNL
   set
       MODIFY_BY = #{modifyBy}
       , MODIFY_DATE = SYSDATE
       , flag = flag + 1
 where 1=1
   and RID_PROM = #{campaignId}

/* LoyCampaignMapper.updateCampaignContentsChnnelRel */
update LOY.LOY_PROM_ALT_CHNL
   set
       MODIFY_BY = #{modifyBy}
       , MODIFY_DATE = SYSDATE
       , FLAG = 1
       , RID_PROM = #{campaignId}
       , RID_CONTS = #{contentsId}
       , ALT_CHNL_TYPE_CD = (select exe_chnl_type_cd from  com.mkt_conts_mst where id = #{contentsId})

       , EXP_SEND_DATE = #{exeTm}
       , EXP_TYPE      = #{exeType}
 where RID = #{id}

mapper.insert:
/* LoyCampaignMapper.insertCampaignContentsChnnelRel */
insert into LOY.LOY_PROM_ALT_CHNL
    ( RID
    , CREATE_BY
    , MODIFY_BY
    , CREATE_DATE
    , MODIFY_DATE
    , FLAG
    , RID_PROM
    , RID_CONTS
    , ALT_CHNL_TYPE_CD

    , EXP_SEND_DATE
    , EXP_TYPE)
values
    ( #{id}
    , #{createBy}
    , #{modifyBy}
    , SYSDATE
    , SYSDATE
    , 1
    , #{campaignId}
    , #{contentsId}


    , (select exe_chnl_type_cd from  com.mkt_conts_mst where id = #{contentsId})
    , #{exeTm}
    , #{exeType} 
    )



    select id from LOY.LOY_PROM_ALT_CHNL where id = #{id} OFFSET 0 ROWS FETCH NEXT 1 ROWS ONLY
